import React from 'react';
import axios from 'axios';
import CustomForm from '../components/Form';

import { Button, Card } from 'antd';

class ScanDetail extends React.Component{

    state = {
        match: {}
    }

    componentDidMount() {
        const matchID = this.props.match.params.matchID;
        axios.get('http://127.0.0.1:8000/scans/' + matchID + '/')
            .then(res => {
                this.setState({
                    match: res.data
                });
            })
            .catch(err => {
                err.response.status == '404' ? 
                    this.props.history.push('/')
                :
                    console.log('loading')
            })
    }

    handleDelete = (event) => {
        const matchID = this.props.match.params.matchID;
        axios.delete('http://127.0.0.1:8000/scans/' + matchID + '/');
    }

    render(){
        return (
           <div>
                <Card title={this.state.match.title}>
                    <p>{this.state.match.description}</p>
                </Card>
                {
                this.props.match.params.matchID !== null ? 
                
                    this.state.match

                :

                    <span>No data fdound</span>
                }
           </div>
        )
    }
}

export default ScanDetail;
