import React from 'react';
import { Row, Col, Avatar, Upload, Button } from 'antd';
import axios from 'axios';
import { UserOutlined } from '@ant-design/icons';


const props = {
    action: 'https://www.mocky.io/v2/5cc8019d300000980a055e76',
    transformFile(file) {
      return new Promise(resolve => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => {
          const canvas = document.createElement('canvas');
          const img = document.createElement('img');
          img.src = reader.result;
          img.onload = () => {
            const ctx = canvas.getContext('2d');
            ctx.drawImage(img, 0, 0);
            ctx.fillStyle = 'red';
            ctx.textBaseline = 'middle';
            ctx.fillText('Ant Design', 20, 20);
            canvas.toBlob(resolve);
          };
        };
      });
    },
  };


class UserProfile extends React.Component{

    state = {
        profile_pic: []
    }

    componentDidMount() {
        axios.get('http://localhost:8000/userprofile/img-salt-fetch/' + localStorage.getItem('userProfileID'))
            .then(
                
            res => {
                setTimeout(() => {
                    this.setState({profile_pic: res.data}
                    ,function(){
                        console.log(this.state);
                    }
                    );
                }, 100)
            }

            //     res => {
            //     this.setState({
            //         profile_pic: res.data
            //     });
            // },
            // () => console.log(this.state)
            )
            .catch(err => {
                err.response.status == '404' ? 
                    this.props.history.push('/')
                :
                    console.log('loading')
            })
    }

    render(){
        return (
            <Row align="top">
            <Col xs={{ span: 5, offset: 1 }} lg={{ span: 6, offset: 2 }}>
            <Avatar shape="square" size="large" icon={this.state ==null ? <UserOutlined /> : ''} src={this.state == null ? '' : ''}/>
            <Button>
                <UploadOutlined /> Upload
            </Button>
            </Col>
            <Col xs={{ span: 11, offset: 1 }} lg={{ span: 12, offset: 2 }}>
              asd
            </Col>
          </Row>
        )
    }
}

export default UserProfile;
