import React, { useState, useEffect }  from 'react';
import { Modal, Form, Input, Row, Col, Avatar, Upload, Button, message, Skeleton, Space, Typography  } from 'antd';
import axios from 'axios';
import { UserOutlined, UploadOutlined } from '@ant-design/icons';



function UserProfile() {
    const[username, setUsername] = useState('');
    const[email, setEmail] = useState('');
    const[name, setName] = useState('');
    const[location, setLocation] = useState('');
    const[img_salt, setImgSalt] = useState('');

    const { Text } = Typography;


    const typeOfImage = (proc) => {
        return {"type" : "profile_pic", "process": proc}
    }
    
    const userProfilePictureUploadProps = {
        name: 'file',
        acceptedFiles: '.png',
        multiple: false,
        method: 'post',
        data: typeOfImage("upload"),
        action: 'http://127.0.0.1:8000/file-handler/',
        onRemove(file){

            console.log(file);
            
            axios.post('http://127.0.0.1:8000/file-handler/', {
                "file": file.name,
                ...typeOfImage('remove')
            });

        },
        
        onChange(info) {
          const { status } = info.file;
          if (status !== 'uploading') {
            // console.log(info.file, info.fileList);
          }
          if (status === 'done') {
            message.success(`${info.file.name} file uploaded successfully.`);
            
            // Reload image
            
            reloadProfilePicture();

            
          } else if (status === 'error') {
            message.error(`${info.file.name} file upload failed.`);
          }
        },
      };

    const getProfile = () => {
    axios.get('http://localhost:8000/userprofile/' + localStorage.getItem('userProfileID'))
    .then( res => {
        // Load all profile data


        reloadUsername(res.data['username']);
        reloadEmail(res.data['email']);
        reloadName(res.data['name']);
        reloadLocation(res.data['location']);
        reloadImgSalt(res.data['img_salt']);



        setTimeout(() => message.success('Profile loaded successfully.'), 100);
        }
    )
    .catch(err => {
        message.error(`Your profile could not be loaded due to ` + err.message);
    })
    }

    const reloadUsername = (data) => {
        setUsername(data);
    }

    const reloadEmail = (data) => {
        setEmail(data);
    }

    const reloadName = (data) => {
        setName(data);
    }

    const reloadLocation = (data) => {
        setLocation(data);
    }

    const reloadImgSalt = (data) => {
        setImgSalt(data);
    }

    const reloadProfilePicture = () => {
        axios.get('http://localhost:8000/file-handler/', {
            name: 'pic.png',
            type: 'profile_pic'
        })
        .then(res => console.log(res))
    }

    const CollectionCreateForm = ({ visible, onCreate, onCancel }) => {
        const [form] = Form.useForm();
        return (
          <Modal
            visible={visible}
            title="Update Profile"
            okText="Update"
            cancelText="Cancel"
            onCancel={onCancel}
            onOk={() => {
              form
                .validateFields()
                .then(values => {
                  form.resetFields();
                  onCreate(values);
                })
                .catch(info => {
                  console.log('Validate Failed:', info);
                });
            }}
          >
            <Form
              form={form}
              layout="vertical"
              name="form_in_modal"
              initialValues={{
                modifier: 'public',
              }}
            >
              <Form.Item
                name="name"
                label="Name"
                rules={[
                  {
                    message: 'Please input your full name.',
                  },
                ]}
              >
                <Input placeholder={name}/>
              </Form.Item>

              <Form.Item
                name="emil"
                label="Email"
                rules={[
                  {
                    message: 'Please input your email.',
                    type: 'email'
                  },
                ]}
              >
                <Input placeholder={email} />
              </Form.Item>

              <Form.Item
                name="location"
                label="Location"
                rules={[
                  {
                    message: 'Please input your Location.',
                  },
                ]}
              >
                <Input placeholder={location}/>
              </Form.Item>
            </Form>
          </Modal>
        );
      };


        const [visible, setVisible] = useState(false);
    
      
        const onCreate = values => {
            console.log('Received values of form: ', values);
            
            var newEmail = values.email == null ? email : values.email;
            var newName = values.name == null ? name : values.name;
            var newLocation = values.location == null ? location : values.location;
            
            axios.put('http://127.0.0.1:8000/userprofile/update/' + localStorage.getItem('userProfileID'),{
                email: newEmail,
                name: newName,
                location: newLocation
            })
            .then(res => {
                reloadEmail(res.data['email']);
                reloadName(res.data['name']);
                reloadLocation(res.data['location']);
                reloadImgSalt(res.data['img_salt']);
                setTimeout(() => message.success('Profile updated successfully!'), 100);
            })
            .catch(err => {
                message.error(`Your profile could not be updated due to ` + err.message);
            })

            
            setVisible(false);
        }


    useEffect(() => {
        getProfile();
    }, []); 


    return (
            <div>   
                <Row gutter={[8, 48]}>
                    <Col xs={{ span: 6, offset: 2 }} lg={{ span: 6, offset: 2 }}>
                        <Avatar shape="square" size="large" icon={img_salt == null ? <UserOutlined /> : ''} src={img_salt == null ? '' : ''}/>
                    </Col>
                    <Col xs={{ span: 5, offset: 1 }} lg={{ span: 6, offset: 2 }}>
                        <h1>Your Profile</h1>
                    </Col>
                </Row>

                <Row gutter={[8, 20]}>
                    <Col xs={{ span: 6, offset: 2 }} lg={{ span: 6, offset: 2 }}>
                        <Upload {...userProfilePictureUploadProps}>
                            <Button
                                size="small">
                                Change
                            </Button>
                        </Upload>
                    </Col>
                    <Col xs={{ span: 8, offset: 1 }} lg={{ span: 6, offset: 2 }}>
                        <Space size = "large">
                            <p>Username: </p>
                            <p>{username == null ? <Text type="danger">No username found!</Text> : username}</p>
                        </Space>
                    </Col>
                </Row>

                <Row gutter={[8, 20]}>
                    <Col xs={{ span: 6, offset: 2 }} lg={{ span: 6, offset: 2 }}>
                    </Col>
                    <Col xs={{ span: 8, offset: 1 }} lg={{ span: 6, offset: 2 }}>
                        <Space size = "large">
                            <p>Name: </p>
                            <p>{name == null ? <Text type="danger">No name found!</Text> : name}</p>
                        </Space>                
                    </Col>
                </Row>

                <Row gutter={[8, 20]}>
                    <Col xs={{ span: 6, offset: 2 }} lg={{ span: 6, offset: 2 }}>
                    </Col>
                    <Col xs={{ span: 8, offset: 1 }} lg={{ span: 6, offset: 2 }}>
                        <Space size = "large">
                            <p>Email: </p>
                            <p>{email == null ? <Skeleton active /> : email}</p>
                        </Space>
                    </Col>
                </Row>

                <Row gutter={[8, 20]}>
                    <Col xs={{ span: 6, offset: 2 }} lg={{ span: 6, offset: 2 }}>
                    </Col>
                    <Col xs={{ span: 8, offset: 1 }} lg={{ span: 6, offset: 2 }}>
                        <Space size = "large">
                            <p>Location: </p>
                            <p>{location == null ? <Text type="danger">No location set!</Text> : location}</p>
                        </Space>                
                    </Col>
                </Row>

                <Row gutter={[8, 20]}>
                    <Col xs={{ span: 6, offset: 2 }} lg={{ span: 6, offset: 2 }}>
                    </Col>

                    <Col xs={{ span: 8, offset: 1 }} lg={{ span: 6, offset: 2 }}>
                        <Button type="primary" onClickCapture={() => setVisible(true)}>Edit</Button>
                    </Col>
                </Row>

                    <CollectionCreateForm
                        visible={visible}
                        onCreate={onCreate}
                        onCancel={() => {
                        setVisible(false);
                        }}
                    />
            </div>
    )
}

export default UserProfile;
