import React, { useState, useEffect }  from 'react';
import { Row, Col, Avatar, Upload, Button, message, Skeleton } from 'antd';
import axios from 'axios';
import { UserOutlined, UploadOutlined } from '@ant-design/icons';

function UserProfile() {

    var profile_pic = null;
    var profile = null;
    const[count, setCount] = useState(0);
    const[profileDetails, setProfileDetails] = useState([])


    const typeOfImage = (proc) => {
        return {"type" : "profile_pic", "process": proc}
    }
    
    const userProfilePictureUploadProps = {
        name: 'file',
        acceptedFiles: '.png',
        multiple: false,
        method: 'post',
        data: typeOfImage("upload"),
        action: 'http://127.0.0.1:8000/image-handler/',
        onRemove(file){

            console.log(file);
            
            axios.post('http://127.0.0.1:8000/image-handler/', {
                "file": file.name,
                ...typeOfImage('remove')
            });

        },
        
        onChange(info) {
          const { status } = info.file;
          if (status !== 'uploading') {
            // console.log(info.file, info.fileList);
          }
          if (status === 'done') {
            message.success(`${info.file.name} file uploaded successfully.`);
          } else if (status === 'error') {
            message.error(`${info.file.name} file upload failed.`);

            reloadProfilePic();
          }
        },
      };

      const getProfile = () => {
        axios.get('http://localhost:8000/userprofile/profile/' + localStorage.getItem('userProfileID'))
        .then( res => {
            profile = res.data;
            profile_pic = res.data.img_salt;
            reloadProfileDetail();
            setTimeout(() => console.log(profile), 100);
            }
        )
        .catch(err => {
            message.error(`Your profile could not be loaded due to ` + err.message);
        })
      }

      const reloadProfilePic = () => {
        setCount(count + 15)
      }

      const reloadProfileDetail = () => {
        setCount(count + 15)
      }

      useEffect(() => {
        getProfile();
        reloadProfilePic();
      }, []); 


        return (
            <Row align="top">
            <Col xs={{ span: 5, offset: 1 }} lg={{ span: 6, offset: 2 }}>
            <Avatar shape="square" size="large" icon={profile_pic == null ? <UserOutlined /> : ''} src={profile_pic == null ? '' : ''}/>
            
            <Row
            style={{ margin: '50px 0px', verticalAlign: 'middle' }}>
                <Upload {...userProfilePictureUploadProps}>
                <Button
                    size="small">
                    Change
                </Button>
                </Upload>
            </Row>
           
            </Col>
            <Col xs={{ span: 11, offset: 1 }} lg={{ span: 12, offset: 2 }}>
                {profile == null ? <Skeleton active /> : 'form'}
            </Col>
            <p>You clicked {count} times</p>
            <p>{profile}</p>
            <button onClick={() => setCount(count + 1)}>
                Click me
            </button>
          </Row>
        )
}

export default UserProfile;
