import React from 'react';
import axios from 'axios';
import CustomForm from '../components/Form';

import { Button, Card, Row, Col } from 'antd';

class ScanDetail extends React.Component{

    state = {
        match: {},
        structure: null
    }

    componentDidMount() {
        const matchID = this.props.match.params.matchID;
        axios.get('http://127.0.0.1:8000/scans/' + matchID + '/')
            .then(res => {
                if(res.status == 200){
                    this.setState({
                        match: res.data
                    });
                    
                    axios.get('http://127.0.0.1:8000/metrics-structure/')
                    .then(res => {
                        if(res.status == 200){
                            this.setState({structure: res.data});
                        }
                    })
                }
            })
            .catch(err => {
                // err.response.status == '404' ? 
                //     this.props.history.push('/')
                // :
                //     console.log('loading')
            })
    }

    handleDelete = (event) => {
        const matchID = this.props.match.params.matchID;
        axios.delete('http://127.0.0.1:8000/scans/' + matchID + '/');
    }

    // Function to get the match details

    fetchMatch = () => {
        const matchID = this.props.match.params.matchID;
        axios.get('http://localhost:8000/scans/' + matchID)
        .then(res => {
            this.setState({match: res.data})
        })
    }

    // ----------------------------------------------------------------







    displayMetrics = () => {
        var cat = this.state.structure[0][0];

        var output = ""

        for(var i=0;i<this.state.structure[0].length;i++){
            output += "<h1>" + cat + "</h1>"
        }

        console.log(this.state.match)
        console.log(this.state.structure)
        return output;
    }

    render(){
        return (
           <div>
                {
                this.props.match.params.matchID !== null ? 
                
                    <div>
                        <Row>
                            <Col span={12}>
                                <h1>Research</h1>
                                <h2></h2>

                            </Col>
                            <Col span={12}>col-12</Col>
                        </Row>
                    </div>

                :
                    
                    <span>No data found</span>
                }
           </div>
        )
    }
}

export default ScanDetail;
