import React from 'react';
import { Row, Col, Avatar } from 'antd';
import axios from 'axios';
import { UserOutlined } from '@ant-design/icons';


class UserProfile extends React.Component{

    state = {
        img_salt: ''
    }

    componentDidMount() {
        axios.get('http://localhost:8000/userprofile/img-salt-fetch/' + localStorage.getItem('userProfileID'))
        .then(res => {
            console.log(res.data.img_salt)
            this.setState({
                img_salt: 'res.data.img_salt'
            })
        })
        .finally(console.log(this.state))
    }

    state = {
        jobpost: {}
    }

    componentDidMount() {
        const jobpostID = this.props.match.params.jobpostID;
        axios.get('http://127.0.0.1:8000/jobpost/' + jobpostID + '/')
            .then(res => {
                this.setState({
                    jobpost: res.data
                });
            })
            .catch(err => {
                err.response.status == '404' ? 
                    this.props.history.push('/')
                :
                    console.log('loading')
            })
    }

    render(){
        return (
            <Row align="top">
            <Col xs={{ span: 5, offset: 1 }} lg={{ span: 6, offset: 2 }}>
            <Avatar shape="square" size="large" icon={<UserOutlined />} />
            </Col>
            <Col xs={{ span: 11, offset: 1 }} lg={{ span: 12, offset: 2 }}>
              Col
            </Col>
          </Row>
        )
    }
}

export default UserProfile;
