import React from 'react';
import { List } from 'antd';
import { EditOutlined, DeleteOutlined } from '@ant-design/icons';
import { Card,Progress } from 'antd';
import {Link } from 'react-router-dom';

// {id, projectid, userid, jobid, matchpercent}

const { Meta } = Card;

const parseDate = (date) => {
    parts = date.split('-')
    var d = new Date(parts[0], parts[1]);
    return date;
}
const Scan = (props) => {
    return (
        <List
        grid={{
          gutter: 16,
          xs: 1,
          sm: 2,
          md: 4,
          lg: 4,
          xl: 6,
          xxl: 3,
        }}
        
        pagination={{
            onChange: page => {
                // console.log(page);
            },
            pageSize: 10,
            }}
        dataSource={props.data}
        renderItem={item => (
          <List.Item>

            <Link to={"scan/" + item.id}>
            <Card title={item.title}
            hoverable
            loading={item ? false : true}


        cover={
            <Progress type="circle" percent={item.matchpercent? item.matchpercent : 0} />
          }

          actions={[
            <DeleteOutlined key="delete" />,
            <EditOutlined key="edit" />,
          ]}
        >
            
            <Meta
                // avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                title= {item.title ? item.title : "No position details"}
                description={"Created on " + parseDate(item.posted_date)}
            />
            </Card>
            </Link>
          </List.Item>
        )}
      />
  )
}

export default Scan;
