import React from 'react';
import { Row, Col, Avatar, Upload, Button, message } from 'antd';
import axios from 'axios';
import { UserOutlined, UploadOutlined } from '@ant-design/icons';

const type = () => {
    return {"type" : "profile_pic",}
}

const userProfilePictureUploadProps = {
    name: 'file',
    acceptedFiles: '.png',
    multiple: false,
    method: 'post',
    data: type,
    action: 'http://127.0.0.1:8000/upload-image/',
    onChange(info) {
      const { status } = info.file;
      if (status !== 'uploading') {
        // console.log(info.file, info.fileList);
      }
      if (status === 'done') {
        message.success(`${info.file.name} file uploaded successfully.`);
      } else if (status === 'error') {
        message.error(`${info.file.name} file upload failed.`);
      }
    },
  };


function UserProfile() {


        return (
            <Row align="top">
            <Col xs={{ span: 5, offset: 1 }} lg={{ span: 6, offset: 2 }}>
            <Avatar shape="square" size="large" icon={this.state ==null ? <UserOutlined /> : ''} src={this.state == null ? '' : ''}/>
            
            <Row
            style={{ margin: '50px 0px', verticalAlign: 'middle' }}>
                <Upload {...userProfilePictureUploadProps}>
                <Button
                    size="small">
                    Change
                </Button>
                </Upload>
            </Row>
           
            </Col>
            <Col xs={{ span: 11, offset: 1 }} lg={{ span: 12, offset: 2 }}>
              asd
            </Col>
          </Row>
        )
}

export default UserProfile;
