import React, { useState, useEffect }  from 'react';
import { Modal, Form, Input, Row, Col, Avatar, Upload, Button, message, Skeleton, Space, Typography  } from 'antd';
import axios from 'axios';
import { UserOutlined, UploadOutlined } from '@ant-design/icons';

function UserProfile() {

    const[username, setUsername] = useState('');
    const[email, setEmail] = useState('');
    const[name, setName] = useState('');
    const[location, setLocation] = useState('');
    const[img_salt, setImgSalt] = useState('');
    const[confirmLoading, setconfirmLoading] = useState(false);
    
    const[visible, setVisible] = useState(false);

    const { Text } = Typography;

    const layout = {
        labelCol: { span: 8 },
        wrapperCol: { span: 16 },
    };
    const validateMessages = {
        required: '${label} is required!',
        types: {
          email: '${label} is not validate email!',
          number: '${label} is not a validate number!',
        },
        number: {
          range: '${label} must be between ${min} and ${max}',
        },
      };


    const typeOfImage = (proc) => {
        return {"type" : "profile_pic", "process": proc}
    }
    
    const userProfilePictureUploadProps = {
        name: 'file',
        acceptedFiles: '.png',
        multiple: false,
        method: 'post',
        data: typeOfImage("upload"),
        action: 'http://127.0.0.1:8000/image-handler/',
        onRemove(file){

            console.log(file);
            
            axios.post('http://127.0.0.1:8000/image-handler/', {
                "file": file.name,
                ...typeOfImage('remove')
            });

        },
        
        onChange(info) {
          const { status } = info.file;
          if (status !== 'uploading') {
            // console.log(info.file, info.fileList);
          }
          if (status === 'done') {
            message.success(`${info.file.name} file uploaded successfully.`);
          } else if (status === 'error') {
            message.error(`${info.file.name} file upload failed.`);
            // Reload image
            
            reloadProfilePicture();
          }
        },
      };

    const getProfile = () => {
    axios.get('http://localhost:8000/userprofile/profile/' + localStorage.getItem('userProfileID'))
    .then( res => {
        // Load all profile data


        reloadUsername(res.data['username']);
        reloadEmail(res.data['email']);
        reloadName(res.data['name']);
        reloadLocation(res.data['location']);
        reloadImgSalt(res.data['img_salt']);



        setTimeout(() => message.success('Profile loaded successfully.'), 100);
        }
    )
    .catch(err => {
        message.error(`Your profile could not be loaded due to ` + err.message);
    })
    }

    const reloadUsername = (data) => {
        setUsername(data);
    }

    const reloadEmail = (data) => {
        setEmail(data);
    }

    const reloadName = (data) => {
        setName(data);
    }

    const reloadLocation = (data) => {
        setLocation(data);
    }

    const reloadImgSalt = (data) => {
        setImgSalt(data);
    }

    const reloadProfilePicture = () => {

    }

    const onFinish = values => {
        console.log(values);
    }

    const showModal = () => {
        setVisible(true);
    }

    const changeConfirmLoading = () => {
        setconfirmLoading(true);
    }

    const handleOk = () => {
        setTimeout(() =>  {
            setVisible(false);
            setconfirmLoading(false);
            console.log(confirmLoading);
        }, 2000);
      };

      const handleCancel = () => {
        console.log('Clicked cancel button');
        this.setState({
          visible: false,
        });
      };


    useEffect(() => {
        getProfile();
    }, []); 


    return (
            <div>   
                <Row gutter={[8, 48]}>
                    <Col xs={{ span: 6, offset: 2 }} lg={{ span: 6, offset: 2 }}>
                        <Avatar shape="square" size="large" icon={img_salt == null ? <UserOutlined /> : ''} src={img_salt == null ? '' : ''}/>
                    </Col>
                    <Col xs={{ span: 5, offset: 1 }} lg={{ span: 6, offset: 2 }}>
                        <h1>Your Profile</h1>
                    </Col>
                </Row>

                <Row gutter={[8, 20]}>
                    <Col xs={{ span: 6, offset: 2 }} lg={{ span: 6, offset: 2 }}>
                        <Upload {...userProfilePictureUploadProps}>
                            <Button
                                size="small">
                                Change
                            </Button>
                        </Upload>
                    </Col>
                    <Col xs={{ span: 8, offset: 1 }} lg={{ span: 6, offset: 2 }}>
                        <Space size = "large">
                            <p>Username: </p>
                            <p>{username == null ? <Text type="danger">No username found!</Text> : username}</p>
                        </Space>
                    </Col>
                </Row>

                <Row gutter={[8, 20]}>
                    <Col xs={{ span: 6, offset: 2 }} lg={{ span: 6, offset: 2 }}>
                    </Col>
                    <Col xs={{ span: 8, offset: 1 }} lg={{ span: 6, offset: 2 }}>
                        <Space size = "large">
                            <p>Name: </p>
                            <p>{name == null ? <Text type="danger">No name found!</Text> : name}</p>
                        </Space>                
                    </Col>
                </Row>

                <Row gutter={[8, 20]}>
                    <Col xs={{ span: 6, offset: 2 }} lg={{ span: 6, offset: 2 }}>
                    </Col>
                    <Col xs={{ span: 8, offset: 1 }} lg={{ span: 6, offset: 2 }}>
                        <Space size = "large">
                            <p>Email: </p>
                            <p>{email == null ? <Skeleton active /> : email}</p>
                        </Space>
                    </Col>
                </Row>

                <Row gutter={[8, 20]}>
                    <Col xs={{ span: 6, offset: 2 }} lg={{ span: 6, offset: 2 }}>
                    </Col>
                    <Col xs={{ span: 8, offset: 1 }} lg={{ span: 6, offset: 2 }}>
                        <Space size = "large">
                            <p>Location: </p>
                            <p>{location == null ? <Text type="danger">No location set!</Text> : location}</p>
                        </Space>                
                    </Col>
                </Row>

                <Row gutter={[8, 20]}>
                    <Col xs={{ span: 6, offset: 2 }} lg={{ span: 6, offset: 2 }}>
                    </Col>

                    <Col xs={{ span: 8, offset: 1 }} lg={{ span: 6, offset: 2 }}>
                        <Button type="primary" onClickCapture={showModal}>Edit</Button>
                    </Col>
                </Row>

                <Modal
                    title="Title"
                    visible={visible}
                    onOk={handleOk}
                    confirmLoading={confirmLoading}
                    onCancel={handleCancel}
                    >
                    <Form {...layout} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages}>
                        <Form.Item name={['user', 'name']} label="Name" rules={[{ required: true }]}>
                            <Input />
                        </Form.Item>
                        <Form.Item name={['user', 'email']} label="Email" rules={[{ type: 'email' }]}>
                            <Input />
                        </Form.Item>
                    </Form>
                </Modal>
            </div>
    )
}

export default UserProfile;

