import React from 'react';
import { Row, Col, Avatar, Upload, Button, message } from 'antd';
import axios from 'axios';
import { UserOutlined, UploadOutlined } from '@ant-design/icons';


const userProfilePictureUploadProps = {
    name: 'file',
    multiple: false,
    action: 'http://127.0.0.1:8000/upload-image/',
    onChange(info) {
      const { status } = info.file;
      if (status !== 'uploading') {
        // console.log(info.file, info.fileList);
      }
      if (status === 'done') {
        message.success(`${info.file.name} file uploaded successfully.`);
      } else if (status === 'error') {
        message.error(`${info.file.name} file upload failed.`);
      }
    },
  };


class UserProfile extends React.Component{

    state = {
        profile_pic: []
    }

    componentDidMount() {
        axios.get('http://localhost:8000/userprofile/img-salt-fetch/' + localStorage.getItem('userProfileID'))
            .then(
                
            res => {
                setTimeout(() => {
                    this.setState({profile_pic: res.data}
                    ,function(){
                        console.log(this.state);
                    }
                    );
                }, 100)
            }

            //     res => {
            //     this.setState({
            //         profile_pic: res.data
            //     });
            // },
            // () => console.log(this.state)
            )
            .catch(err => {
                err.response.status == '404' ? 
                    this.props.history.push('/')
                :
                    console.log('loading')
            })
    }

    render(){
        return (
            <Row align="top">
            <Col xs={{ span: 5, offset: 1 }} lg={{ span: 6, offset: 2 }}>
            <Avatar shape="square" size="large" icon={this.state ==null ? <UserOutlined /> : ''} src={this.state == null ? '' : ''}/>
            
            <Upload {...userProfilePictureUploadProps}>
            <Button
                size="small"
                style={{ margin: '0 16px', verticalAlign: 'middle' }}>
                Change
            </Button>
            </Upload>
           
           
            </Col>
            <Col xs={{ span: 11, offset: 1 }} lg={{ span: 12, offset: 2 }}>
              asd
            </Col>
          </Row>
        )
    }
}

export default UserProfile;
