import React from 'react';
import axios from 'axios';
import CustomForm from '../components/Form';
import MetricDisplay from '../components/Metrics';


import { Button, Card, Row, Col } from 'antd';

class ScanDetail extends React.Component{

    state = {
        match: {},
        structure: null,
        processed: null
    }

    componentDidMount() {
        const matchID = this.props.match.params.matchID;
        axios.get('http://127.0.0.1:8000/scans/' + matchID + '/')
            .then(res => {
                if(res.status == 200){
                    this.setState({
                        match: res.data
                    });
                    
                    axios.get('http://127.0.0.1:8000/metrics-structure/')
                    .then(res => {
                        if(res.status == 200){
                            this.setState({structure: res.data});
                        }
                    })
                }
            })
            .catch(err => {
                // err.response.status == '404' ? 
                //     this.props.history.push('/')
                // :
                //     console.log('loading')
            })
    }

    handleDelete = (event) => {
        const matchID = this.props.match.params.matchID;
        axios.delete('http://127.0.0.1:8000/scans/' + matchID + '/');
    }

    // Function to get the match details

    // ----------------------------------------------------------------







    displayMetrics = (type) => {
        var res = [subcats, labels]
        var row = 0            
        var subcats = []
        var labels = []

        for(var i=0;i<this.state.match['jobpost_results'].length;i++){
            subcats= []
            labels = []

            var elem = this.state.match['jobpost_results'][i]
            var parts = elem.split(',');
            if(row != parts[0]){
                row = parts[0]
            }
            else{
                continue;
            }
            if(this.state.structure[0][parts[0]].toLowerCase() === type){
                // item.push(this.state.structure[0][parts[0]]); // Catergory
                subcats.push(this.state.structure[1][parts[1]]); // Sub-catergory
                labels.push(this.state.structure[3][parts[0]]); // Label
            }
        }

        console.log(this.state.match);
        console.log(this.state.structure);

        return [subcats, labels];
    }

    render(){
        return (
           <div>
                {
                this.props.match.params.matchID !== null ? 
                
                    <div>
                        { this.state.structure ?
                        <div>
                            <h1>Research</h1>
                                <MetricDisplay data={this.displayMetrics("research")} />
                        </div>
                                :
                        ''
                        }
                    </div>

                :
                    
                    <span>No data found</span>
                }
           </div>
        )
    }
}

export default ScanDetail;
