import React from 'react';
import { Form, Input, Button, DatePicker, Upload } from 'antd';
import { UploadOutlined, InboxOutlined } from '@ant-design/icons';


import axios from 'axios';

const layout = {
  labelCol: {
    span: 6,
  },
  wrapperCol: {
    span: 14,
  },
};
const validateMessages = {
  required: 'This field is required!',
  types: {
    number: 'Not a valid number!',
  },
};

const normFile = e => {
  console.log('Upload event:', e);
  console.log('Upload filelkist:', e.fileList);

  if (Array.isArray(e)) {
    return e;
  }

  return e && e.fileList;
};


class CustomForm extends React.Component {

    constructor(props){
      super(props);
      this.state = {
        err: null
      }
    }

    componentDidMount = () => {
      // console.log(this.props);
    }

    handleFormSubmit = (event, requestType, jobpostID) => {
        event.preventDefault();
        const title = event.target.elements.title.value;
        const description = event.target.elements.description.value;
        const photo = event.target.elements.files;
        const expiry_date = event.target.elements.expiry_date.value;
        const job_poster_id = localStorage.getItem('userProfileID');
        
        console.log(photo)
        switch(requestType){
            case 'post':
               return axios.post('http://127.0.0.1:8000/jobpost/', {
                    title: title,
                    description: description,
                    job_poster_id: job_poster_id,
                    // photo:photo
                })
                .then(res => {
                  console.log(res);
                  if(res.status == '201'){
                    this.props.history.push('/jobpost/' + res.data.id + '/');
                  }
                })
                .catch(error => this.setState({err: error}));

            case 'put':
                return axios.put('http://127.0.0.1:8000/jobpost/' + jobpostID + '/', {
                    title: title,
                    description: description,
                    job_poster_id: job_poster_id,
                    // photo:photo
                })
                .then(res => console.log(res))
                .catch(error => console.error(error))
                .finally(f => window.location.reload());
                
        }
    }

    render(){
        return (
            <Form onSubmitCapture={(event) => this.handleFormSubmit(
                event,
                this.props.requestType,
                this.props.jobpostID
                )} {...layout} name="nest-messages" validateMessages={validateMessages}>
              <Form.Item
                label="Title"
                rules={[
                  {
                    required: true,
                  },
                ]}
              >
                <Input name="title" placeholder={
                  this.props.requestType == 'put' ?
                    this.props.jobpost.title
                  :
                    "Enter a title"
                  }/>
              </Form.Item>
        
              <Form.Item label="Description"
              rules={[
                  {
                    required: true,
                    message:'Please enter an image url'},
              ]}>
                <Input.TextArea name="description" placeholder={
                  this.props.requestType == 'put' ?
                    this.props.jobpost.description
                  :
                    "Enter a Description"
                  }/>
              </Form.Item>

              <Form.Item 
              label="Image"
              
              rules={[
                {
                  required: true,
                  message: 'Please input image url!',
                },
                ]}
              >
                <Form.Item valuePropName="fileList" getValueFromEvent={normFile} noStyle>
                  <Upload.Dragger name="files">
                    <p className="ant-upload-drag-icon">
                      <UploadOutlined />
                    </p>
                    <p className="ant-upload-text">Click or drag image to this area to upload</p>
                    <p className="ant-upload-hint">This will show as your profile picture.</p>
                  </Upload.Dragger>
                </Form.Item>
              </Form.Item>

              
              {/* <Form.Item
                label="Image"
                rules={[
                  {
                    required: true,
                    message: 'Please input image url!',
                  },
                ]}
              >
                  <Input name="img"  placeholder={
                  this.props.requestType == 'put' ?
                    this.props.jobpost.img
                  :
                    "Enter image url"
                  }/>
              </Form.Item> */}





        
              <Form.Item label="Expires On" 
              rules={[
                  {required:true,}
              ]}>
                <DatePicker name="expiry_date" />
              </Form.Item>

              <Form.Item
                hidden={this.state.err == null ? true : false}
              >
              </Form.Item>
             
              <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
                <Button type="primary" htmlType="submit">
                  {this.props.btnText}
                </Button>
              </Form.Item>
            </Form>
          );
    }
 
}

export default CustomForm;
